import React, { useState } from 'react';
import './Counseling.css';

const Counseling = () => {
  const [selectedCounselor, setSelectedCounselor] = useState(null);
  const [bookingStep, setBookingStep] = useState(1);

  const counselors = [
    { 
      id: 1, 
      name: 'Dr. Sarah Smith', 
      specialty: 'Technology & IT Careers', 
      experience: '8 years',
      rating: '4.9',
      availability: ['Monday', 'Wednesday', 'Friday'],
      image: 'üë©‚Äçüíº'
    },
    { 
      id: 2, 
      name: 'Mr. John Davis', 
      specialty: 'Business & Management', 
      experience: '12 years',
      rating: '4.8',
      availability: ['Tuesday', 'Thursday'],
      image: 'üë®‚Äçüíº'
    }
  ];

  return (
    <div className="counseling-page">
      <div className="container">
        <h1>Book Career Counseling Session</h1>
        <p>Connect with expert career counselors to guide your journey</p>
        
        {bookingStep === 1 && (
          <div className="counselor-selection">
            <h2>Choose Your Counselor</h2>
            <div className="counselors-grid">
              {counselors.map(counselor => (
                <div 
                  key={counselor.id} 
                  className={`counselor-card ${selectedCounselor?.id === counselor.id ? 'selected' : ''}`}
                  onClick={() => setSelectedCounselor(counselor)}
                >
                  <div className="counselor-image">{counselor.image}</div>
                  <h3>{counselor.name}</h3>
                  <p className="specialty">{counselor.specialty}</p>
                  <p className="experience">Experience: {counselor.experience}</p>
                  <p className="rating">‚≠ê {counselor.rating}</p>
                  <div className="availability">
                    <strong>Available:</strong> {counselor.availability.join(', ')}
                  </div>
                </div>
              ))}
            </div>
            
            {selectedCounselor && (
              <button 
                className="next-btn"
                onClick={() => setBookingStep(2)}
              >
                Next: Choose Date & Time
              </button>
            )}
          </div>
        )}

        {bookingStep === 2 && (
          <div className="booking-details">
            <h2>Schedule Your Session with {selectedCounselor.name}</h2>
            <p>Date and time selection will go here</p>
            <div className="back-next-buttons">
              <button onClick={() => setBookingStep(1)}>‚Üê Back</button>
              <button>Confirm Booking</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Counseling;
